// src/api.js
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api'; // Assicurati che questa URL sia corretta

export const fetchBooks = () => {
    return axios.get(`${API_BASE_URL}/books/`)
        .then(response => response.data)
        .catch(error => {
            console.error('Error fetching books:', error);
            throw error;
        });
};

export const fetchBookById = (id) => {
    return axios.get(`${API_BASE_URL}/books/${id}/`)
        .then(response => response.data)
        .catch(error => {
            console.error('Error fetching book by id:', error);
            throw error;
        });
};

export const createBook = (book) => {
    return axios.post(`${API_BASE_URL}/books/`, book)
        .then(response => response.data)
        .catch(error => {
            console.error('Error creating book:', error);
            throw error;
        });
};

export const updateBook = (id, book) => {
    return axios.put(`${API_BASE_URL}/books/${id}/`, book)
        .then(response => response.data)
        .catch(error => {
            console.error('Error updating book:', error);
            throw error;
        });
};

export const deleteBook = (id) => {
    return axios.delete(`${API_BASE_URL}/books/${id}/`)
        .then(response => response.data)
        .catch(error => {
            console.error('Error deleting book:', error);
            throw error;
        });
};
