import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchBookById } from '../api'; // Assicurati che questo sia corretto
// Rimuovi o correggi questa importazione se il file non esiste
// import './BookDetailPage.css'; 

const BookDetailPage = () => {
    const { id } = useParams(); // Ottieni l'ID dal parametro dell'URL
    const [book, setBook] = useState(null);

    useEffect(() => {
        fetchBookById(id)
            .then(data => setBook(data))
            .catch(error => console.error('Error fetching book details:', error));
    }, [id]);

    if (!book) {
        return <div>Loading...</div>;
    }

    return (
        <div className="book-detail-page">
            <h1>{book.title}</h1>
            <p><strong>Author:</strong> {book.author}</p>
            <p><strong>Publication Year:</strong> {book.publication_year}</p>
            <p><strong>Price:</strong> ${book.price.toFixed(2)}</p>
            <p><strong>Description:</strong> {book.description || 'No description available.'}</p>
            {/* Aggiungi altre informazioni o dettagli se disponibili */}
        </div>
    );
};

export default BookDetailPage;
